function Button({ children, onClick, variant = 'primary' }) {
  const styles = {
    primary: { background: '#3b82f6', color: 'white' },
    secondary: { background: '#6b7280', color: 'white' },
    danger: { background: '#ef4444', color: 'white' },
  }

  return (
    <button
      onClick={onClick}
      style={{
        ...styles[variant],
        padding: '0.5rem 1rem',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer',
        margin: '0.25rem',
        fontSize: '1rem',
      }}
    >
      {children}
    </button>
  )
}

export default Button
