import { useReducer } from 'react'

// 1. Define initial state
const initialState = {
  count: 0,
  step: 1,
  history: []
}

// 2. Define reducer function
function counterReducer(state, action) {
  switch (action.type) {
    case 'INCREMENT':
      return {
        ...state,
        count: state.count + state.step,
        history: [...state.history, state.count + state.step]
      }
    case 'DECREMENT':
      return {
        ...state,
        count: state.count - state.step,
        history: [...state.history, state.count - state.step]
      }
    case 'SET_STEP':
      return {
        ...state,
        step: action.payload
      }
    case 'RESET':
      return initialState
    default:
      throw new Error(`Unknown action: ${action.type}`)
  }
}

// 3. Use in component
function Counter() {
  const [state, dispatch] = useReducer(counterReducer, initialState)

  return (
    <div className="p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4">Count: {state.count}</h2>

      <div className="flex gap-2 mb-4">
        <button
          onClick={() => dispatch({ type: 'DECREMENT' })}
          className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
        >
          - {state.step}
        </button>
        <button
          onClick={() => dispatch({ type: 'INCREMENT' })}
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
        >
          + {state.step}
        </button>
      </div>

      <div className="flex items-center gap-2 mb-4">
        <label className="text-sm text-gray-600">Step:</label>
        <input
          type="number"
          value={state.step}
          onChange={(e) => dispatch({ type: 'SET_STEP', payload: Number(e.target.value) })}
          className="w-20 px-2 py-1 border rounded"
          min="1"
        />
      </div>

      <button
        onClick={() => dispatch({ type: 'RESET' })}
        className="text-sm text-gray-500 hover:text-gray-700"
      >
        Reset
      </button>

      {state.history.length > 0 && (
        <div className="mt-4 text-sm text-gray-500">
          History: {state.history.join(' ‚Üí ')}
        </div>
      )}
    </div>
  )
}

import { createContext, useContext, useState, useEffect } from 'react'

// 1. Create the context
const ThemeContext = createContext(null)

// 2. Create a provider component
export function ThemeProvider({ children }) {
  const [theme, setTheme] = useState(() => {
    // Check localStorage or system preference
    const saved = localStorage.getItem('theme')
    if (saved) return saved
    return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light'
  })

  useEffect(() => {
    localStorage.setItem('theme', theme)
    // Update document class for Tailwind dark mode
    document.documentElement.classList.toggle('dark', theme === 'dark')
  }, [theme])

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light')
  }

  const value = {
    theme,
    toggleTheme,
    isDark: theme === 'dark'
  }

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  )
}

// 3. Create a custom hook for easy access
export function useTheme() {
  const context = useContext(ThemeContext)
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider')
  }
  return context
}

export default ThemeContext

