import { createContext, useContext, useReducer, useEffect } from 'react'

// Action types as constants (prevents typos)
const ACTIONS = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM',
  UPDATE_QUANTITY: 'UPDATE_QUANTITY',
  CLEAR_CART: 'CLEAR_CART',
  LOAD_CART: 'LOAD_CART'
}

// Initial state
const initialState = {
  items: [],
  totalItems: 0,
  totalPrice: 0
}

// Helper to calculate totals
function calculateTotals(items) {
  return {
    totalItems: items.reduce((sum, item) => sum + item.quantity, 0),
    totalPrice: items.reduce((sum, item) => sum + (item.price * item.quantity), 0)
  }
}

// Reducer function
function cartReducer(state, action) {
  switch (action.type) {
    case ACTIONS.ADD_ITEM: {
      const existingIndex = state.items.findIndex(item => item.id === action.payload.id)

      let newItems
      if (existingIndex >= 0) {
        // Item exists, increase quantity
        newItems = state.items.map((item, index) =>
          index === existingIndex
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      } else {
        // New item
        newItems = [...state.items, { ...action.payload, quantity: 1 }]
      }

      return { ...state, items: newItems, ...calculateTotals(newItems) }
    }

    case ACTIONS.REMOVE_ITEM: {
      const newItems = state.items.filter(item => item.id !== action.payload)
      return { ...state, items: newItems, ...calculateTotals(newItems) }
    }

    case ACTIONS.UPDATE_QUANTITY: {
      const { id, quantity } = action.payload
      if (quantity <= 0) {
        const newItems = state.items.filter(item => item.id !== id)
        return { ...state, items: newItems, ...calculateTotals(newItems) }
      }
      const newItems = state.items.map(item =>
        item.id === id ? { ...item, quantity } : item
      )
      return { ...state, items: newItems, ...calculateTotals(newItems) }
    }

    case ACTIONS.CLEAR_CART:
      return initialState

    case ACTIONS.LOAD_CART:
      return { ...action.payload }

    default:
      throw new Error(`Unknown action: ${action.type}`)
  }
}

// Create context
const CartContext = createContext(null)

// Provider component
export function CartProvider({ children }) {
  const [state, dispatch] = useReducer(cartReducer, initialState)

  // Load cart from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem('cart')
    if (saved) {
      dispatch({ type: ACTIONS.LOAD_CART, payload: JSON.parse(saved) })
    }
  }, [])

  // Save cart to localStorage on change
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(state))
  }, [state])

  // Action creators (cleaner API for components)
  const addItem = (item) => dispatch({ type: ACTIONS.ADD_ITEM, payload: item })
  const removeItem = (id) => dispatch({ type: ACTIONS.REMOVE_ITEM, payload: id })
  const updateQuantity = (id, quantity) => dispatch({ type: ACTIONS.UPDATE_QUANTITY, payload: { id, quantity } })
  const clearCart = () => dispatch({ type: ACTIONS.CLEAR_CART })

  const value = {
    ...state,
    addItem,
    removeItem,
    updateQuantity,
    clearCart
  }

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  )
}

// Custom hook
export function useCart() {
  const context = useContext(CartContext)
  if (!context) {
    throw new Error('useCart must be used within a CartProvider')
  }
  return context
}

export { ACTIONS }

