import { createContext, useContext, useReducer } from 'react'

const DashboardContext = createContext(null)

const initialState = {
  activeWidget: null,
  sidebarOpen: true,
  layout: 'grid' // or 'list'
}

function dashboardReducer(state, action) {
  switch (action.type) {
    case 'SET_ACTIVE_WIDGET':
      return { ...state, activeWidget: action.payload }
    case 'TOGGLE_SIDEBAR':
      return { ...state, sidebarOpen: !state.sidebarOpen }
    case 'SET_LAYOUT':
      return { ...state, layout: action.payload }
    default:
      return state
  }
}

export function DashboardProvider({ children }) {
  const [state, dispatch] = useReducer(dashboardReducer, initialState)

  const setActiveWidget = (id) => dispatch({ type: 'SET_ACTIVE_WIDGET', payload: id })
  const toggleSidebar = () => dispatch({ type: 'TOGGLE_SIDEBAR' })
  const setLayout = (layout) => dispatch({ type: 'SET_LAYOUT', payload: layout })

  return (
    <DashboardContext.Provider value={{ ...state, setActiveWidget, toggleSidebar, setLayout }}>
      {children}
    </DashboardContext.Provider>
  )
}

export function useDashboard() {
  const context = useContext(DashboardContext)
  if (!context) throw new Error('useDashboard must be used within DashboardProvider')
  return context
}

import { useState, useEffect } from 'react'

function WindowTracker() {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth)

  useEffect(() => {
    // 1. Setup
    const handleResize = () => {
      console.log('Resized!')
      setWindowWidth(window.innerWidth)
    }

    window.addEventListener('resize', handleResize)

    // 2. Cleanup (CRITICAL)
    // Without this, you get memory leaks and multiple listeners
    return () => {
      console.log('Cleaning up...')
      window.removeEventListener('resize', handleResize)
    }
  }, []) // Empty array = run once on mount

  return <h1>Window Width: {windowWidth}</h1>
}

import { useRef } from 'react'

function FocusInput() {
  const inputRef = useRef(null)
  const renderCount = useRef(0)

  // This updates immediately but doesn't cause a re-render visual update
  renderCount.current++

  const focusElement = () => {
    // Access the DOM node directly
    inputRef.current.focus()
    inputRef.current.style.backgroundColor = '#f0f9ff'
  }

  return (
    <div>
      <p>Component render count: {renderCount.current}</p>
      <input ref={inputRef} type="text" placeholder="Click button to focus" />
      <button onClick={focusElement}>Focus Input</button>
    </div>
  )
}

import { useState, useEffect } from 'react'

export function useLocalStorage(key, initialValue) {
  // 1. Initialize state lazily
  const [value, setValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key)
      return item ? JSON.parse(item) : initialValue
    } catch (error) {
      console.error(error)
      return initialValue
    }
  })

  // 2. Update localStorage when state changes
  useEffect(() => {
    try {
      window.localStorage.setItem(key, JSON.stringify(value))
    } catch (error) {
      console.error(error)
    }
  }, [key, value])

  return [value, setValue]
}

