import gsap from 'gsap'
import { useRef } from 'react'

function Box() {
  const boxRef = useRef()

  useGSAP(() => {
    gsap.to(boxRef.current, { 
      x: 200, 
      rotation: 360, 
      duration: 2, 
      ease: "power2.out" 
    })
  })

  return <div ref={boxRef} className="w-20 h-20 bg-green-500" />
}

useGSAP(() => {
  const tl = gsap.timeline()

  tl.to('.box1', { x: 100, duration: 1 })
    .to('.box2', { x: 100, duration: 1 }, "-=0.5") // Overlap by 0.5s
    .to('.box3', { rotation: 180 })
})

useGSAP(() => {
  gsap.to(ref.current, {
    x: 500,
    scrollTrigger: {
      trigger: ref.current,
      start: "top center", // when top of element hits center of viewport
      end: "bottom top",
      scrub: 1, // Smooth scrubbing (1s lag)
      markers: true // Dev tools helper
    }
  })
})

import { ScrollControls, Scroll, useScroll } from '@react-three/drei'

function Model() {
  const scroll = useScroll()
  useFrame((state) => {
    // scroll.offset is between 0 and 1
    const offset = scroll.offset
    state.camera.position.z = 5 - (offset * 10)
  })
  return <mesh ... />
}

export default function Scene() {
  return (
    <Canvas>
      <ScrollControls pages={3} damping={0.3}>
        <Model />
        <Scroll html>
           <h1>HTML Content Overlay</h1>
        </Scroll>
      </ScrollControls>
    </Canvas>
  )
}

